<?php
function FacebookLogin(){
    //Facebook initialization

    global $facebook;

    $user = $facebook->getUser();
    if ($user) {
        try {
            // Proceed knowing you have a logged in user who's authenticated.
            $user_profile = $facebook->api('/me');
        } catch (FacebookApiException $e) {
            error_log($e);
            $user = null;
        }
    }

    $loginUrl = $facebook->getLoginUrl(array('scope' => 'publish_stream, email', 'redirect_uri' => HOSTNAME.'setsession.php'));

    echo '<a href="' . $loginUrl . '"><img class="facebook-img" src="data:image/png;base64," /></a>';
    //echo '<img class="facebook-img" src="data:image/png;base64," style="filter: url(img/filters.svg#grayscale); filter: gray; -webkit-filter: grayscale(1);"/><br/><p style="color:red; font-size:14px;">The site is under maintenance. We are sorry for the inconvenience.</p>';
}
?>